mapper.select:
select SBC.BANK_CHARGES_ID, SBC.BANK_ID, SBC.MARKET_ID, SBC.BONUS_FROM, SBC.BONUS_TO, SBC.CHARGES, SBC.OBJECT_VERSION_NUMBER, SBC.REQUEST_ID, SBC.PROGRAM_ID, SBC.CREATION_DATE, SBC.CREATED_BY, SBC.LAST_UPDATED_BY, SBC.LAST_UPDATE_DATE, SBC.LAST_UPDATE_LOGIN, SBC.ATTRIBUTE_CATEGORY, SBC.CHARGE_TYPE from SPM_BANK_CHARGES sbc left outer join SPM_BANK_B sbb on(sbc.BANK_ID=sbb.BANK_ID)
sbc.BANK_ID= #{bankId,jdbcType=DECIMAL}
order by SBC.BONUS_TO asc

select count(1) from SPM_BANK_CHARGES sbc
sbc.BANK_ID=#{bankId,jdbcType=DECIMAL}
and SBC.BANK_CHARGES_ID != #{bankChargesId,jdbcType=DECIMAL}
AND ((#{bonusFrom,jdbcType=DECIMAL} >= SBC.BONUS_FROM AND #{bonusFrom,jdbcType=DECIMAL} < SBC.BONUS_TO ) OR ( #{bonusTo,jdbcType=DECIMAL} > SBC.BONUS_FROM AND #{bonusTo,jdbcType=DECIMAL} <= SBC.BONUS_TO ) OR ( SBC.BONUS_FROM >= #{bonusFrom,jdbcType=DECIMAL} AND SBC.BONUS_FROM < #{bonusTo,jdbcType=DECIMAL} ) OR ( SBC.BONUS_TO > #{bonusFrom,jdbcType=DECIMAL} AND SBC.BONUS_TO <= #{bonusTo,jdbcType=DECIMAL} ) )
