select scb.COMPANY_ID, scb.CODE, sct.NAME, scb.COMPANY_TYPE, sct.SHORT_NAME, scb.CONTACTS, scb.PHONE, scb.FAX, sct.REMARK, scb.EMAIL, scb.LOCATION_ID, scb.AREA_CODE, scb.BR_NO, scb.PRIVATESUBPARTER, scb.PARTER, scb.PUBLICSUBPARTER, scb.GST_ID, scb.ATTRIBUTE1, scb.ATTRIBUTE2 from SPM_COMPANY_B scb left join SPM_COMPANY_TL sct on (sct.COMPANY_ID = scb.COMPANY_ID AND sct.lang = #{request.locale,jdbcType=VARCHAR})
1=1
AND scb.COMPANY_ID = #{companyId,jdbcType=DECIMAL}
AND (SCB.COMPANY_ID = (SELECT b.COMPANY_ID FROM SPM_MARKET_B b WHERE b.MARKET_ID =#{request.marketId,jdbcType=DECIMAL}) OR SCB.GST_ID=(SELECT b.COMPANY_ID FROM SPM_MARKET_B b WHERE b.MARKET_ID =#{request.marketId,jdbcType=DECIMAL}))
AND Upper(scb.CODE） like concat('%',concat( Upper( #{code,jdbcType=VARCHAR}),'%'))
AND Upper(sct.NAME) like concat('%',concat( Upper( #{name,jdbcType=VARCHAR}),'%'))
and scb.COMPANY_TYPE = #{companyType,jdbcType=VARCHAR}
ORDER BY ${_colName} ${sortorder}

select scb.COMPANY_ID, scb.CODE, sct.NAME, sct.SHORT_NAME, scb.CONTACTS, scb.PHONE, scb.FAX, sct.REMARK, scb.EMAIL, scb.LOCATION_ID, scb.AREA_CODE, scb.BR_NO, scb.PRIVATESUBPARTER, scb.PARTER, scb.PUBLICSUBPARTER, scb.GST_ID, scb.ATTRIBUTE1, scb.ATTRIBUTE2, scb.ATTRIBUTE3 from SPM_COMPANY_B scb left join SPM_COMPANY_TL sct on (sct.COMPANY_ID = scb.COMPANY_ID AND sct.lang = #{request.locale,jdbcType=VARCHAR})
1=1
AND scb.COMPANY_ID = #{companyId,jdbcType=DECIMAL}
AND (SCB.COMPANY_ID = (SELECT b.COMPANY_ID FROM SPM_MARKET_B b WHERE b.MARKET_ID =#{request.marketId,jdbcType=DECIMAL}) OR SCB.COMPANY_ID=(SELECT SCB.GST_ID FROM SPM_COMPANY_B scb WHERE SCB.COMPANY_ID = (SELECT b.COMPANY_ID FROM SPM_MARKET_B b WHERE b.MARKET_ID = #{request.marketId,jdbcType=DECIMAL})) ) AND scb.COMPANY_TYPE='HDQTR'
AND Upper(scb.CODE） like concat('%',concat( Upper( #{code,jdbcType=VARCHAR}),'%'))
AND Upper(sct.NAME) like concat('%',concat( Upper( #{name,jdbcType=VARCHAR}),'%'))
and scb.COMPANY_TYPE = #{companyType,jdbcType=VARCHAR}
ORDER BY ${_colName} ${sortorder}

