mapper.select:
select b.defination_id, b.code, t.name, t.description, b.org_type, b.location_id, b.enabled_flag, b.start_active_date, b.end_active_date, b.sales_org_id, b.inv_org_id, b.sales_org_flag, b.inv_org_flag, b.cost_org_id, b.company_id, cost_inv_ids.name cost_org_name, sc.name as company_name from spm_org_defination_b b left outer join ( select cost_tl.name, cost_b.inv_org_id from spm_org_defination_b cost_b left outer join spm_org_defination_tl cost_tl on (cost_b.defination_id = cost_tl.defination_id and cost_tl.lang = #{request.locale,jdbcType=VARCHAR}) ) cost_inv_ids on cost_inv_ids.inv_org_id = b.cost_org_id left join spm_org_defination_tl t on (b.defination_id = t.defination_id and t.lang = #{request.locale,jdbcType=VARCHAR}) left outer join SPM_COMPANY_B sc on(b.COMPANY_ID=sc.COMPANY_ID)
(b.end_active_date > current_timestamp or b.end_active_date is null)
AND Upper(b.code) like concat('%', concat(Upper(#{code,jdbcType=VARCHAR}), '%'))
AND Upper(t.name) like concat('%',concat(Upper(#{name,jdbcType=VARCHAR}),'%'))
AND b.org_type = #{orgType,jdbcType=VARCHAR}
AND b.sales_org_flag = #{salesOrgFlag,jdbcType=VARCHAR}
AND b.inv_org_flag = #{invOrgFlag,jdbcType=VARCHAR}
order by b.code ${sortorder}
order by t.name ${sortorder}

select b.defination_id, b.code, t.name, t.description, b.org_type, b.location_id, b.enabled_flag, b.start_active_date, b.end_active_date, b.sales_org_id, b.inv_org_id, b.sales_org_flag, b.inv_org_flag, b.cost_org_id, b.company_id, cost_inv_ids.name cost_org_name, sc.name as company_name from spm_org_defination_b b left outer join ( select cost_tl.name, cost_b.inv_org_id from spm_org_defination_b cost_b left outer join spm_org_defination_tl cost_tl on (cost_b.defination_id = cost_tl.defination_id and cost_tl.lang = #{request.locale,jdbcType=VARCHAR}) ) cost_inv_ids on cost_inv_ids.inv_org_id = b.cost_org_id left join spm_org_defination_tl t on (b.defination_id = t.defination_id and t.lang = #{request.locale,jdbcType=VARCHAR}) left outer join SPM_COMPANY_B sc on(b.COMPANY_ID=sc.COMPANY_ID)
(b.end_active_date > current_timestamp or b.end_active_date is null) AND (sc.COMPANY_ID = (SELECT b.COMPANY_ID FROM SPM_MARKET_B b WHERE b.MARKET_ID =#{request.marketId,jdbcType=DECIMAL}) OR sc.GST_ID=(SELECT b.COMPANY_ID FROM SPM_MARKET_B b WHERE b.MARKET_ID =#{request.marketId,jdbcType=DECIMAL}))
AND Upper(b.code) like concat('%', concat(Upper(#{code,jdbcType=VARCHAR}), '%'))
AND Upper(t.name) like concat('%',concat(Upper(#{name,jdbcType=VARCHAR}),'%'))
AND b.org_type = #{orgType,jdbcType=VARCHAR}
AND b.sales_org_flag = #{salesOrgFlag,jdbcType=VARCHAR}
AND b.inv_org_flag = #{invOrgFlag,jdbcType=VARCHAR}
order by b.code ${sortorder}
order by t.name ${sortorder}

select b.defination_id, b.code, t.name, t.description, b.location_id, b.sales_org_id, b.inv_org_id, b.sales_org_flag, b.inv_org_flag from spm_org_defination_b b left join spm_org_defination_tl t on (b.defination_id = t.defination_id and t.lang = #{request.locale,jdbcType=VARCHAR})
b.SALES_ORG_FLAG = 'Y' AND b.ENABLED_FLAG = 'Y'
AND b.SALES_ORG_ID = #{salesOrgId, jdbcType=VARCHAR}
AND b.SALES_ORG_FLAG = #{salesOrgFlag, jdbcType=VARCHAR}

select count(0)
from spm_org_defination_b b 
where b.code = #{orgCode,jdbcType=VARCHAR}
