mapper.select:
select

from SPM_SALES_ORGANIZATION_B
where SALES_ORG_ID = #{salesOrgId,jdbcType=DECIMAL}

mapper.update:
update SPM_SALES_ORGANIZATION_B
set  PARENT_ORG_ID = #{parentOrgId,jdbcType=DECIMAL},
  MARKET_ID = #{marketId,jdbcType=DECIMAL}
where SALES_ORG_ID = #{salesOrgId,jdbcType=DECIMAL}

mapper.select:
select b.MARKET_ID, b.SALES_ORG_ID, b.CODE, tl.NAME, tl.DESCRIPTION,b.location_id from SPM_SALES_ORGANIZATION_B b left join SPM_SALES_ORGANIZATION_TL tl on (tl.SALES_ORG_ID = b.SALES_ORG_ID AND tl.lang = #{request.locale,jdbcType=VARCHAR}) LEFT OUTER JOIN SPM_ORG_DEFINATION_B sod on b.SALES_ORG_ID=SOD.SALES_ORG_ID left outer join SPM_COMPANY_B sc on sod.COMPANY_ID=sc.COMPANY_ID
b.market_id is null and b.PARENT_ORG_ID is null and (b.END_ACTIVE_DATE > CURRENT_TIMESTAMP or b.END_ACTIVE_DATE is null)
AND b.SALES_ORG_ID = #{salesOrgId,jdbcType=VARCHAR}
AND Upper(b.CODE) like concat('%',concat(Upper(#{code,jdbcType=VARCHAR}),'%'))
AND Upper(tl.NAME) like concat('%',concat(Upper(#{name,jdbcType=VARCHAR}),'%'))
AND tl.DESCRIPTION like concat('%',concat(#{description,jdbcType=VARCHAR},'%'))
AND b.market_id = #{marketId,jdbcType=DECIMAL}
ORDER BY ${_colName} ${sortorder}

select b.MARKET_ID, b.SALES_ORG_ID, b.CODE, tl.NAME, tl.DESCRIPTION,b.location_id from SPM_SALES_ORGANIZATION_B b left join SPM_SALES_ORGANIZATION_TL tl on (tl.SALES_ORG_ID = b.SALES_ORG_ID AND tl.lang = 'zh_CN') LEFT OUTER JOIN SPM_ORG_DEFINATION_B sod on b.SALES_ORG_ID=SOD.SALES_ORG_ID left outer join SPM_COMPANY_B sc on sod.COMPANY_ID=sc.COMPANY_ID
(b.end_active_date > current_timestamp or b.end_active_date is null)
AND b.market_id = #{marketId,jdbcType=DECIMAL}

select b.MARKET_ID, b.SALES_ORG_ID, b.CODE, tl.NAME, tl.DESCRIPTION,b.location_id from SPM_SALES_ORGANIZATION_B b left join SPM_SALES_ORGANIZATION_TL tl on (tl.SALES_ORG_ID = b.SALES_ORG_ID AND tl.lang = #{request.locale,jdbcType=VARCHAR}) LEFT OUTER JOIN SPM_ORG_DEFINATION_B sod on b.SALES_ORG_ID=SOD.SALES_ORG_ID left outer join SPM_COMPANY_B sc on sod.COMPANY_ID=sc.COMPANY_ID
(b.end_active_date > current_timestamp or b.end_active_date is null)
AND b.SALES_ORG_ID = #{salesOrgId,jdbcType=VARCHAR}
AND Upper(b.CODE) like concat('%',concat(Upper(#{code,jdbcType=VARCHAR}),'%'))
AND Upper(tl.NAME) like concat('%',concat(Upper(#{name,jdbcType=VARCHAR}),'%'))
AND tl.DESCRIPTION like concat('%',concat(#{description,jdbcType=VARCHAR},'%'))
AND b.market_id = #{marketId,jdbcType=DECIMAL}
ORDER BY ${_colName} ${sortorder}

select b.MARKET_ID, b.SALES_ORG_ID, b.CODE, tl.NAME, tl.DESCRIPTION,b.location_id from SPM_SALES_ORGANIZATION_B b
left join SPM_SALES_ORGANIZATION_TL tl on (tl.SALES_ORG_ID = b.SALES_ORG_ID AND tl.lang = #{request.locale,jdbcType=VARCHAR}) LEFT OUTER JOIN SPM_ORG_DEFINATION_B sod on b.SALES_ORG_ID=SOD.SALES_ORG_ID left outer join SPM_COMPANY_B sc on sod.COMPANY_ID=sc.COMPANY_ID
(b.end_active_date > current_timestamp or b.end_active_date is null) AND (sc.COMPANY_ID = (SELECT b.COMPANY_ID FROM SPM_MARKET_B b WHERE b.MARKET_ID =#{request.marketId,jdbcType=DECIMAL}) OR sc.GST_ID=(SELECT b.COMPANY_ID FROM SPM_MARKET_B b WHERE b.MARKET_ID =#{request.marketId,jdbcType=DECIMAL}))
AND b.SALES_ORG_ID = #{salesOrgId,jdbcType=VARCHAR}
AND Upper(b.CODE) like concat('%',concat(Upper(#{code,jdbcType=VARCHAR}),'%'))
AND Upper(tl.NAME) like concat('%',concat(Upper(#{name,jdbcType=VARCHAR}),'%'))
AND tl.DESCRIPTION like concat('%',concat(#{description,jdbcType=VARCHAR},'%'))
AND b.market_id = #{marketId,jdbcType=DECIMAL}
ORDER BY ${_colName} ${sortorder}

select b.MARKET_ID, b.SALES_ORG_ID, b.CODE, tl.NAME, tl.DESCRIPTION,b.location_id
from SPM_SALES_ORGANIZATION_B b
left join SPM_SALES_ORGANIZATION_TL tl on (tl.SALES_ORG_ID = b.SALES_ORG_ID AND tl.lang =
#{request.locale,jdbcType=VARCHAR})
LEFT JOIN SYS_USER_ROLE_ASSIGN sura on (b.SALES_ORG_ID=sura.ASSIGN_VALUE)

    (b.end_active_date > current_timestamp or b.end_active_date is null)
    and sura.USER_ID = #{request.userId,jdbcType=VARCHAR}
    AND sura.ROLE_ID = #{request.roleId,jdbcType=VARCHAR}
    AND sura.ASSIGN_TYPE = 'SALES'

        AND b.SALES_ORG_ID = #{salesOrgId,jdbcType=VARCHAR}


        AND Upper(b.CODE) like concat('%',concat(Upper(#{code,jdbcType=VARCHAR}),'%'))


        AND Upper(tl.NAME) like concat('%',concat(Upper(#{name,jdbcType=VARCHAR}),'%'))


        AND tl.DESCRIPTION like concat('%',concat(#{description,jdbcType=VARCHAR},'%'))


        AND b.market_id = #{marketId,jdbcType=DECIMAL}




    ORDER BY ${_colName} ${sortorder}

select b.MARKET_ID, b.SALES_ORG_ID, b.CODE, tl.NAME, tl.DESCRIPTION,b.location_id from SPM_SALES_ORGANIZATION_B b left join SPM_SALES_ORGANIZATION_TL tl on (tl.SALES_ORG_ID = b.SALES_ORG_ID AND tl.lang = #{request.locale,jdbcType=VARCHAR})
EXISTS (SELECT 1 FROM sys_user_role_assign sura WHERE sura.assign_type = 'SALES' AND sura.assign_value = b.sales_org_id AND sura.user_id = #{request.userId, jdbcType=DECIMAL} AND sura.role_id = #{request.roleId, jdbcType=DECIMAL}) AND (b.end_active_date > current_timestamp or b.end_active_date is null)
AND b.SALES_ORG_ID = #{salesOrgId,jdbcType=VARCHAR}
AND Upper(b.CODE) like concat('%',concat(Upper(#{code,jdbcType=VARCHAR}),'%'))
AND Upper(tl.NAME) like concat('%',concat(Upper(#{name,jdbcType=VARCHAR}),'%'))
AND tl.DESCRIPTION like concat('%',concat(#{description,jdbcType=VARCHAR},'%'))
AND b.market_id = #{marketId,jdbcType=DECIMAL}
ORDER BY ${_colName} ${sortorder}

