mapper.select:
 select 

from SPM_SUPPLY


        AND SALES_ORG_ID = #{salesOrgId,jdbcType=DECIMAL}


        AND SUPPLY_TYPE = #{supplyType,jdbcType=VARCHAR}


        AND COUNTRY_CODE = #{countryCode,jdbcType=VARCHAR}


        AND COUNTRY_CODE IS NULL


        AND STATE_CODE = #{stateCode,jdbcType=VARCHAR}


        AND STATE_CODE IS NULL 


        AND CITY_CODE = #{cityCode,jdbcType=VARCHAR}


        AND CITY_CODE IS NULL

select s.supply_id, s.supply_type, s.sales_org_id, s.country_code, s.state_code, s.city_code, s.inv_org_id, s.remark, s.default_flag, ct.name as country_name, st.name as state_name, cit.name as city_name, spt.name as sales_org_name, sb.code as sales_org_code, ib.code as inv_org_code, it.name as inv_org_name from spm_supply s left join spm_country_tl ct on (s.country_code = ct.country_code and ct.lang = #{request.locale,jdbcType=VARCHAR}) left join spm_state_tl st on (st.state_code = s.state_code and st.lang = #{request.locale,jdbcType=VARCHAR}) left join spm_city_tl cit on (cit.city_code = s.city_code and cit.lang = #{request.locale,jdbcType=VARCHAR}) left join spm_sales_organization_b sb on sb.sales_org_id = s.sales_org_id left join spm_sales_organization_tl spt on (spt.sales_org_id = s.sales_org_id and spt.lang = #{request.locale,jdbcType=VARCHAR}) left join spm_inv_organization_b ib on ib.inv_org_id = s.inv_org_id left join spm_inv_organization_tl it on (it.inv_org_id = s.inv_org_id and it.lang = #{request.locale,jdbcType=VARCHAR})
AND s.SALES_ORG_ID = #{salesOrgId,jdbcType=DECIMAL}
AND s.SUPPLY_TYPE = #{supplyType,jdbcType=VARCHAR}
AND s.COUNTRY_CODE = #{countryCode,jdbcType=VARCHAR}
AND s.STATE_CODE = #{stateCode,jdbcType=VARCHAR}
AND s.CITY_CODE = #{cityCode,jdbcType=VARCHAR}
AND s.STATE_CODE is null
AND s.CITY_CODE is null
AND s.supply_id = #{supplyId,jdbcType=DECIMAL}

select b.* from (select t.supply_type, t.sales_org_id, t.country_code, t.state_code, t.city_code, (select ct.name from spm_country_tl ct where ct.country_code = t.country_code and ct.lang = #{request.locale,jdbcType=VARCHAR}) as country_name, (select st.name from spm_state_tl st where st.state_code = t.state_code and st.lang = #{request.locale,jdbcType=VARCHAR}) as state_name, (select sct.name from spm_city_tl sct where sct.city_code = t.city_code and sct.lang = #{request.locale,jdbcType=VARCHAR}) as city_name, (select sot.name from spm_sales_organization_tl sot where sot.sales_org_id = t.sales_org_id and sot.lang = #{request.locale,jdbcType=VARCHAR}) as sales_org_name, (select sob.code from spm_sales_organization_b sob where sob.sales_org_id = t.sales_org_id) as sales_org_code from (select s.supply_type, s.sales_org_id, s.country_code, s.state_code, s.city_code from spm_supply s group by (s.supply_type, s.sales_org_id, s.country_code, s.state_code, s.city_code) order by s.supply_type) t) bï¼Œ (SELECT sura.USER_ROLE_ASSIGN_ID, sura.USER_ID, sura.ROLE_ID, sura.ASSIGN_TYPE, sura.ASSIGN_VALUE, sura.DEFAULT_FLAG FROM SYS_USER_ROLE_ASSIGN sura WHERE sura.USER_ID = #{request.userId,jdbcType=VARCHAR} AND sura.ROLE_ID = #{request.roleId,jdbcType=VARCHAR} AND sura.ASSIGN_TYPE = 'SALES') sl
sl.ASSIGN_VALUE=b.SALES_ORG_ID
AND b.SALES_ORG_ID = #{salesOrgId,jdbcType=DECIMAL}
AND b.SUPPLY_TYPE = #{supplyType,jdbcType=VARCHAR}
AND b.COUNTRY_CODE = #{countryCode,jdbcType=VARCHAR}
AND b.STATE_CODE = #{stateCode,jdbcType=VARCHAR}
AND b.CITY_CODE = #{cityCode,jdbcType=VARCHAR}
and Upper(b.country_name) like concat('%',concat(Upper(#{countryName,jdbcType=VARCHAR}),'%'))
and Upper(b.state_name) like concat('%',concat(Upper(#{stateName,jdbcType=VARCHAR}),'%'))
and Upper(b.city_name) like concat('%',concat(Upper(#{cityName,jdbcType=VARCHAR}),'%'))
and Upper(b.sales_org_name) like concat('%',concat(Upper(#{salesOrgName,jdbcType=VARCHAR}),'%'))

select b.* from (select t.supply_type, t.sales_org_id, t.country_code, t.state_code, t.city_code, (select ct.name from spm_country_tl ct where ct.country_code = t.country_code and ct.lang = #{request.locale,jdbcType=VARCHAR}) as country_name, (select st.name from spm_state_tl st where st.state_code = t.state_code and st.lang = #{request.locale,jdbcType=VARCHAR}) as state_name, (select sct.name from spm_city_tl sct where sct.city_code = t.city_code and sct.lang = #{request.locale,jdbcType=VARCHAR}) as city_name, (select sot.name from spm_sales_organization_tl sot where sot.sales_org_id = t.sales_org_id and sot.lang = #{request.locale,jdbcType=VARCHAR}) as sales_org_name, (select sob.code from spm_sales_organization_b sob where sob.sales_org_id = t.sales_org_id) as sales_org_code from (select s.supply_type, s.sales_org_id, s.country_code, s.state_code, s.city_code from spm_supply s group by (s.supply_type, s.sales_org_id, s.country_code, s.state_code, s.city_code) order by s.supply_type) t) b
AND b.SALES_ORG_ID = #{salesOrgId,jdbcType=DECIMAL}
AND b.SUPPLY_TYPE = #{supplyType,jdbcType=VARCHAR}
AND b.COUNTRY_CODE = #{countryCode,jdbcType=VARCHAR}
AND b.STATE_CODE = #{stateCode,jdbcType=VARCHAR}
AND b.CITY_CODE = #{cityCode,jdbcType=VARCHAR}
and Upper(b.country_name) like concat('%',concat(Upper(#{countryName,jdbcType=VARCHAR}),'%'))
and Upper(b.state_name) like concat('%',concat(Upper(#{stateName,jdbcType=VARCHAR}),'%'))
and Upper(b.city_name) like concat('%',concat(Upper(#{cityName,jdbcType=VARCHAR}),'%'))
and Upper(b.sales_org_name) like concat('%',concat(Upper(#{salesOrgName,jdbcType=VARCHAR}),'%'))

select t.supply_type, t.sales_org_id, t.country_code, t.state_code, t.city_code, (select ct.name from spm_country_tl ct where ct.country_code = t.country_code and ct.lang = #{request.locale,jdbcType=VARCHAR}) as country_name, (select st.name from spm_state_tl st where st.state_code = t.state_code and st.lang = #{request.locale,jdbcType=VARCHAR}) as state_name, (select sct.name from spm_city_tl sct where sct.city_code = t.city_code and sct.lang = #{request.locale,jdbcType=VARCHAR}) as city_name, (select sot.name from spm_sales_organization_tl sot where sot.sales_org_id = t.sales_org_id and sot.lang = #{request.locale,jdbcType=VARCHAR}) as sales_org_name, (select sob.code from spm_sales_organization_b sob where sob.sales_org_id = t.sales_org_id) as sales_org_code from (select s.supply_type, s.sales_org_id, s.country_code, s.state_code, s.city_code from spm_supply s group by (s.supply_type, s.sales_org_id, s.country_code, s.state_code, s.city_code) order by s.supply_type) t
AND t.SALES_ORG_ID = #{salesOrgId,jdbcType=DECIMAL}
AND t.SUPPLY_TYPE = #{supplyType,jdbcType=VARCHAR}
AND t.COUNTRY_CODE = #{countryCode,jdbcType=VARCHAR}
AND t.STATE_CODE = #{stateCode,jdbcType=VARCHAR}
AND t.CITY_CODE = #{cityCode,jdbcType=VARCHAR}
AND t.STATE_CODE IS NULL
AND t.CITY_CODE IS NULL

select count(1)
from spm_supply


         SALES_ORG_ID = #{salesOrgId,jdbcType=DECIMAL}


