mapper.select:
SELECT pv.LOT_CONTROL_FLAG 
FROM INV_ITEM_PROPERTY_V pv
WHERE PV.ORGANIZATION_ID = #{orgId,jdbcType = DECIMAL}
AND pv.ITEM_ID = #{itemId, jdbcType = DECIMAL}

SELECT ioq.organization_id, ioq.lot_number, ioq.item_id, sum(ioq.quantity) as quantity, ioq.uom_code, il.expiry_date, min(ioq.init_transaction_id) as transaction_id FROM inv_onhand_quantity ioq LEFT JOIN inv_lot il ON il.lot_number = ioq.lot_number and il.item_id = ioq.item_id and il.organization_id = ioq.organization_id
1=1
AND ioq.organization_id = #{orgId,jdbcType=DECIMAL}
AND ioq.item_id = #{itemId,jdbcType=DECIMAL}
GROUP BY ioq.organization_id, ioq.item_id, ioq.lot_number, ioq.uom_code, il.expiry_date order by transaction_id ASC

SELECT ioq.organization_id, ioq.lot_number, ioq.item_id, sum(ioq.quantity) - coalesce((SELECT SUM(dodlt.outstanding_qty - coalesce(dodlt.return_qty,0)) FROM om_sales_line osl, dm_order_delivery dod, dm_order_delivery_line dodl ,dm_order_delivery_lot dodlt ,inv_item_property_v v WHERE dod.delivery_id = dodl.delivery_id AND dodl.line_id = dodlt.delivery_line_id AND dodl.order_line_id = osl.line_id AND dod.inv_org_id = ioq.organization_id AND osl.item_id = v.item_id and v.organization_id = dod.inv_org_id and v.count_item_id = ioq.item_id AND coalesce(dodlt.lot_number, chr(10)) = coalesce(ioq.lot_number, chr(10)) AND (dod.delivery_status = 'PDDL' or dod.delivery_status = 'NEW')), 0) as quantity, ioq.uom_code, il.expiry_date, min(ioq.init_transaction_id) as transaction_id FROM inv_onhand_quantity ioq LEFT JOIN inv_lot il ON il.lot_number = ioq.lot_number and il.item_id = ioq.item_id and il.organization_id = ioq.organization_id
1=1
AND ioq.organization_id = #{orgId,jdbcType=DECIMAL}
AND ioq.item_id = #{itemId,jdbcType=DECIMAL}
GROUP BY ioq.organization_id, ioq.item_id, ioq.lot_number, ioq.uom_code, il.expiry_date order by il.expiry_date ASC
