mapper.update:
update DM_ORDER_DELIVERY d set d.delivery_status = #{newStatus,jdbcType=VARCHAR}, OBJECT_VERSION_NUMBER = OBJECT_VERSION_NUMBER+1, LAST_UPDATE_DATE = CURRENT_TIMESTAMP where d.delivery_status in
#{state,jdbcType=VARCHAR}
and d.ORDER_HEADER_ID = #{headerId,jdbcType=DECIMAL}

mapper.select:
select count(*) 
from DM_ORDER_DELIVERY  
where ORDER_HEADER_ID = #{headerId,jdbcType=DECIMAL} 
and DELIVERY_STATUS not in 

    #{state,jdbcType=VARCHAR}

select sum(dodl.outstanding_qty)
  from dm_order_delivery_line dodl ,
  dm_order_delivery dod
  where  dod.delivery_id = dodl.delivery_id
  and dod.delivery_status in ('PCKUP','SHIPP')
  and dodl.order_line_id = #{orderLineId,jdbcType=DECIMAL}

select DELIVERY_ID, INV_ORG_ID, ORDER_HEADER_ID, DELIVERY_NUMBER, DELIVERY_TYPE, ( select coalesce(scvbct.meaning, scvb.meaning) from sys_code_b scb, sys_code_value_b scvb left join sys_code_value_tl scvbct on (scvb.code_value_id = scvbct.code_value_id and scvbct.lang = #{request.locale,jdbcType=VARCHAR}) where scb.code = 'DM.DELIVERY_STATUS' and scb.code_id = scvb.code_id and scvb.value = DELIVERY_STATUS) DELIVERY_STATUS, DELIVERY_DATE, REMARK, OBJECT_VERSION_NUMBER, REQUEST_ID, PROGRAM_ID, CREATION_DATE, CREATED_BY, LAST_UPDATED_BY, LAST_UPDATE_DATE, LAST_UPDATE_LOGIN, ATTRIBUTE_CATEGORY, ATTRIBUTE1, ATTRIBUTE2, ATTRIBUTE3, ATTRIBUTE4, ATTRIBUTE5, ATTRIBUTE6, ATTRIBUTE7, ATTRIBUTE8, ATTRIBUTE9, ATTRIBUTE10, ATTRIBUTE11, ATTRIBUTE12, ATTRIBUTE13, ATTRIBUTE14, ATTRIBUTE15, PICK_RELEASE_ID, contact_name, phone ,city_code ,state_code ,country_code ,address ,address1 ,address2 ,address3 ,area_code ,zip_code,RECEIPT_FLAG,INTERNAL_REMARK from DM_ORDER_DELIVERY
INV_ORG_ID = #{request.invOrgId,jdbcType=DECIMAL}
AND DELIVERY_NUMBER like concat('%',concat(#{deliveryNumber,jdbcType=VARCHAR},'%'))
ORDER BY ${_colName} ${sortorder}
