mapper.select:
    select 
    count(1)
    from INV_COST_DETAILS

        INV_ORG_ID IN (
	        SELECT SODB.INV_ORG_ID
			FROM SPM_ORG_DEFINATION_B SODB
			WHERE SODB.COST_ORG_ID = #{invOrgId,jdbcType=DECIMAL}
        )

        AND YEAR = #{year,jdbcType=DECIMAL}


        AND MONTH = #{month,jdbcType=DECIMAL}

select 
count(1)
from INV_COST_DETAILS
where 
INV_ORG_ID = #{invOrgId,jdbcType=DECIMAL}
AND YEAR = #{year,jdbcType=DECIMAL}
AND MONTH = #{month,jdbcType=DECIMAL}
and UNIT_COST is null
AND TRX_TYPE NOT IN ('COMCS','DCMCP', 'DELVY', 'STKOT','TRFOT')

mapper.delete:
DELETE FROM INV_COST_DETAILS
WHERE COST_ORG_ID = #{invOrgId,jdbcType=DECIMAL}
	AND YEAR = #{year,jdbcType=DECIMAL}
	AND MONTH = #{month,jdbcType=DECIMAL}

mapper.select:
SELECT ICD.COST_ORG_ID, ICD.ITEM_ID, IIB.ITEM_NUMBER, IITL.ITEM_NAME, ICD.LOT_NUMBER, ICD.EXPIRY_DATE, ICD.TRX_TYPE, ICD.UNIT_COST, SUM(ICD.QUANTITY) AS QUANTITY, ICD.REMARK FROM INV_COST_DETAILS ICD LEFT OUTER JOIN INV_ITEM_TL IITL ON(ICD.ITEM_ID = IITL.ITEM_ID AND IITL.LANG = #{request.locale,jdbcType=VARCHAR}) LEFT OUTER JOIN INV_ITEM_B IIB ON(ICD.ITEM_ID = IIB.ITEM_ID)
ICD.INV_ORG_ID IN ( SELECT SODB.INV_ORG_ID FROM SPM_ORG_DEFINATION_B SODB WHERE SODB.COST_ORG_ID = #{invOrgId,jdbcType=DECIMAL} ) AND ICD.STATUS = 'N'
AND ICD.TRX_TYPE = 'STKIN'
AND ICD.YEAR = #{year,jdbcType=DECIMAL}
AND ICD.MONTH = #{month,jdbcType=DECIMAL}
GROUP BY (ICD.COST_ORG_ID, ICD.ITEM_ID, IIB.ITEM_NUMBER, IITL.ITEM_NAME, ICD.EXPIRY_DATE, ICD.TRX_TYPE, ICD.REMARK,ICD.LOT_NUMBER, ICD.UNIT_COST)

SELECT ICD.COST_ORG_ID, ICD.ITEM_ID, IIB.ITEM_NUMBER, IITL.ITEM_NAME, ICD.TRX_TYPE, SUM(ICD.QUANTITY) AS QUANTITY, SUM(ICD.UNIT_COST) AS UNIT_COST, ICD.REMARK FROM INV_COST_DETAILS ICD LEFT OUTER JOIN INV_ITEM_TL IITL ON(ICD.ITEM_ID = IITL.ITEM_ID AND IITL.LANG = #{request.locale,jdbcType=VARCHAR}) LEFT OUTER JOIN INV_ITEM_B IIB ON(ICD.ITEM_ID = IIB.ITEM_ID)
ICD.INV_ORG_ID IN ( SELECT SODB.INV_ORG_ID FROM SPM_ORG_DEFINATION_B SODB WHERE SODB.COST_ORG_ID = #{invOrgId,jdbcType=DECIMAL} ) AND ICD.STATUS = 'N'
AND ICD.TRX_TYPE = 'STKIN'
AND ICD.YEAR = #{year,jdbcType=DECIMAL}
AND ICD.MONTH = #{month,jdbcType=DECIMAL}
GROUP BY ICD.COST_ORG_ID, ICD.ITEM_ID, IIB.ITEM_NUMBER, IITL.ITEM_NAME, ICD.TRX_TYPE, ICD.REMARK

    SELECT

    FROM INV_COST_DETAILS

    	INV_ORG_ID IN (
  				SELECT SODB.INV_ORG_ID
				FROM SPM_ORG_DEFINATION_B SODB
				WHERE SODB.COST_ORG_ID = #{invOrgId,jdbcType=DECIMAL}
  		)
    	AND YEAR = #{year,jdbcType=DECIMAL}
    	AND MONTH = #{month,jdbcType=DECIMAL}

    		AND TRX_TYPE = #{trxType, jdbcType=VARCHAR}

    	AND UNIT_COST is null

  	SELECT

  	FROM INV_COST_DETAILS

  		INV_ORG_ID IN (
  				SELECT SODB.INV_ORG_ID
				FROM SPM_ORG_DEFINATION_B SODB
				WHERE SODB.COST_ORG_ID = #{invOrgId,jdbcType=DECIMAL}
  		)
  		AND STATUS != 'C'

	    	AND YEAR = #{year,jdbcType=DECIMAL}


	    	AND MONTH = #{month,jdbcType=DECIMAL}

mapper.update:
    update INV_COST_DETAILS


        UNIT_COST = #{unitCost,jdbcType=DECIMAL},


        REMARK = #{remark,jdbcType=VARCHAR},

        STATUS = 'N',
        OBJECT_VERSION_NUMBER = OBJECT_VERSION_NUMBER + 1,
        LAST_UPDATE_DATE = CURRENT_TIMESTAMP


    	COST_ORG_ID = #{costOrgId,jdbcType=DECIMAL}

	    	and ITEM_ID = #{itemId,jdbcType=DECIMAL}


      		and TRX_TYPE = #{trxType, jdbcType=VARCHAR}


	    	and LOT_NUMBER = #{lotNumber,jdbcType=VARCHAR}


	    	and EXPIRY_DATE = #{expiryDate,jdbcType=TIMESTAMP}

    	and YEAR = #{year,jdbcType=DECIMAL}
    	and MONTH = #{month,jdbcType=DECIMAL}

  	update INV_COST_DETAILS
  	set STATUS = #{status,jdbcType=VARCHAR} 
	where COST_ORG_ID = #{invOrgId,jdbcType=DECIMAL}
		and YEAR = #{year,jdbcType=DECIMAL}
		and MONTH = #{month,jdbcType=DECIMAL}
