mapper.select:
select 

from INV_ONHAND_QUANTITY ioq

  ORGANIZATION_ID = #{organizationId,jdbcType=DECIMAL}
  and ITEM_ID = #{itemId,jdbcType=DECIMAL}

    AND SUBINVENTORY_ID = #{subinventoryId,jdbcType=DECIMAL}


    AND LOCATION_ID = #{locationId,jdbcType=DECIMAL}


    AND LOT_NUMBER = #{lotNumber,jdbcType=VARCHAR}


order by init_transaction_id asc
for update

select 
    i.ITEM_ID 
from 
    INV_ONHAND_QUANTITY_LOT_V i,
    inv_item_hierarchy iih
where
    i.LOT_NUMBER = #{lotNumber,jdbcType=VARCHAR}
    and i.ORGANIZATION_ID =#{organizationId, jdbcType=DECIMAL}
    and iih.PARENT_ITEM_ID = #{packageItemId,jdbcType=DECIMAL}
    and iih.ITEM_ID = i .ITEM_ID

SELECT SUM(ioq.quantity) FROM inv_onhand_quantity ioq left join INV_ITEM_PROPERTY_V itpv on itpv.count_item_id = ioq.item_id and cast(itpv.organization_id as number) = ioq.organization_id
ioq.organization_id IN (SELECT spma.INV_ORG_ID FROM SPM_SUPPLY spma WHERE spma.SALES_ORG_ID = #{organizationId,jdbcType=DECIMAL}) and itpv.ITEM_ID = #{itemId,jdbcType=DECIMAL}
AND ioq.SUBINVENTORY_ID = #{subinventoryId,jdbcType=DECIMAL}
AND ioq.LOCATION_ID = #{locationId,jdbcType=DECIMAL}
AND ioq.LOT_NUMBER = #{lotNumber,jdbcType=VARCHAR}
GROUP BY ioq.organization_id ,itpv.item_id
,ioq.subinventory_id
,ioq.location_id
,ioq.lot_number

SELECT oso.ORDER_NUMBER lotNumber,osl.QUANTITY itemId
FROM OM_SALES_ORDER oso, OM_SALES_LINE osl

  OSO.HEADER_ID = OSL.HEADER_ID
  AND OSO.SALES_ORG_ID = #{organizationId,jdbcType=DECIMAL}
  AND OSL.ITEM_ID = #{itemId,jdbcType=DECIMAL}
  AND OSO.ORDER_STATUS = 'IEAP'

     SELECT SUM(ioqq.quantity-ioqq.pending_qty)
	  FROM inv_onhand_quantity_qry_v ioqq

	 	ioqq.organization_id = #{organizationId,jdbcType=DECIMAL}
	 	AND ioqq.item_id = #{itemId,jdbcType=DECIMAL}

        AND ioqq.lot_number = #{lotNumber,jdbcType=VARCHAR}

SELECT iit.organization_id, iit.item_id, iit.item_number, iit.item_name, iit.description item_desc, ioqq.subinventory_id, ioqq.location_id, ioqq.lot_number, ioqq.uom_code, ioqq.quantity, ioqq.pending_qty, (ioqq.quantity - ioqq.pending_qty) available_qty, ioqq.lot_expiry_date expiryDate, ioqq.currency_code, ioqq.unit_cost, (ioqq.unit_cost * ioqq.quantity) cost, (SELECT coalesce(iuot.name, iuo.name) FROM inv_unit_of_measure_b iuo, inv_unit_of_measure_tl iuot WHERE iuo.uom_code = iuot.uom_code AND iuo.uom_code = ioqq.uom_code AND iuot.lang = #{request.locale,jdbcType=VARCHAR}) uom_name FROM inv_onhand_quantity_qry_v ioqq, inv_item_tl_v iit
ioqq.organization_id = iit.organization_id AND ioqq.item_id = iit.item_id AND iit.lang = #{request.locale,jdbcType=VARCHAR}
AND iit.ORGANIZATION_ID = #{organizationId,jdbcType=DECIMAL}
AND ioqq.SUBINVENTORY_ID = #{subinventoryId,jdbcType=DECIMAL}
AND ioqq.LOCATION_ID = #{locationId,jdbcType=DECIMAL}
AND iit.item_id = #{itemId,jdbcType=DECIMAL}
AND upper(iit.item_number) like concat('%',concat(upper(#{itemNumber,jdbcType=VARCHAR}),'%'))
AND ioqq.lot_expiry_date >= #{expiryDateFrom,javaType=java.util.Date}
AND ioqq.lot_expiry_date <= #{expiryDateTo,javaType=java.util.Date}
AND upper(ioqq.LOT_NUMBER) like concat('%',concat(upper(#{lotNumber,jdbcType=VARCHAR}),'%'))
ORDER BY iit.organization_id, iit.item_number, ioqq.lot_number, ioqq.quantity

SELECT iit.organization_id, iit.item_id, iit.item_number, iit.item_name, iit.description item_desc, ioqq.subinventory_id, ioqq.location_id, ioqq.lot_number, ioqq.uom_code, ioqq.quantity, ioqq.pending_qty, (ioqq.quantity - ioqq.pending_qty) available_qty, ioqq.lot_expiry_date expiryDate, ioqq.currency_code, ioqq.unit_cost, (ioqq.unit_cost * ioqq.quantity) cost, (SELECT coalesce(iuot.name, iuo.name) FROM inv_unit_of_measure_b iuo, inv_unit_of_measure_tl iuot WHERE iuo.uom_code = iuot.uom_code AND iuo.uom_code = ioqq.uom_code AND iuot.lang = #{request.locale,jdbcType=VARCHAR}) uom_name FROM inv_onhand_quantity_qryb_v ioqq, inv_item_tl_v iit
ioqq.organization_id = iit.organization_id AND ioqq.item_id = iit.item_id AND iit.lang = #{request.locale,jdbcType=VARCHAR}
AND iit.ORGANIZATION_ID = #{organizationId,jdbcType=DECIMAL}
AND ioqq.SUBINVENTORY_ID = #{subinventoryId,jdbcType=DECIMAL}
AND ioqq.LOCATION_ID = #{locationId,jdbcType=DECIMAL}
AND iit.item_id = #{itemId,jdbcType=DECIMAL}
AND upper(iit.item_number) like concat('%',concat(upper(#{itemNumber,jdbcType=VARCHAR}),'%'))
AND ioqq.lot_expiry_date >= #{expiryDateFrom,javaType=java.util.Date}
AND ioqq.lot_expiry_date <= #{expiryDateTo,javaType=java.util.Date}
AND upper(ioqq.LOT_NUMBER) like concat('%',concat(upper(#{lotNumber,jdbcType=VARCHAR}),'%'))
ORDER BY iit.organization_id, iit.item_number, ioqq.lot_number, ioqq.quantity

 select ioqq.ORGANIZATION_ID, ioqq.ITEM_ID, ioqq.LOT_NUMBER, (ioqq.QUANTITY - ioqq.PENDING_QTY) as available_qty
 from inv_onhand_quantity_qry_v ioqq
where ORGANIZATION_ID = #{organizationId, jdbcType=DECIMAL}
