mapper.select:
select

from INV_TRANSFER_TRX_DETAIL


        TRX_Id = #{trxId,jdbcType=DECIMAL}


        AND TRX_DETAIL_ID = #{trxDetailId,jdbcType=DECIMAL}


        AND SOURCE_LINE_ID = #{sourceLineId,jdbcType=DECIMAL}

select t1.TRX_DETAIL_ID, t1.TRX_ID, t1.STATUS, t1.ORGANIZATION_ID, t1.SUBINVENTORY_ID, t1.LOCATOR_ID, t1.ITEM_ID, t1.LOT_NUMBER, t1.EXPIRY_DATE, t1.PACKING_TYPE, t1.NUMBER_OF_CARTON, t1.NUMBER_OF_IND_CARTON, t1.REMAINING_IND_AFT_CAR, t1.TRX_QTY, t1.SOURCE_LINE_ID, t1.REMARK,
t1.TRX_DETAIL_ID inTrxDetailId, t2.TRX_DETAIL_ID outTrxDetailId, t1.TRX_QTY inTrxQty, t2.TRX_QTY outTrxQty, t2.REMAINING_IND_AFT_CAR remainingQty, item.ITEM_NUMBER itemNumber, item.ITEM_NAME itemName, item.UOM_CODE itemUomCode
from INV_TRANSFER_TRX_DETAIL t1, INV_TRANSFER_TRX_DETAIL t2, INV_ITEM_B item
t1.TRX_Id = #{trxId,jdbcType=DECIMAL}
AND t1.TRX_DETAIL_ID = #{trxDetailId,jdbcType=DECIMAL}
AND t1.SOURCE_LINE_ID=t2.TRX_DETAIL_ID AND t1.ITEM_ID=item.ITEM_ID

    	select sum(coalesce(t2.trx_qty, 0)) TRX_QTY
		from inv_transfer_trx_detail t2
		where
		t2.trx_id in (
		  select t1.trx_id FROM inv_transfer_trx t1
		  where t1.source_trx_id = #{sourceTrxId, jdbcType=DECIMAL}
		  and trx_id != #{trxId, jdbcType=DECIMAL}
		)
        and t2.item_id = #{itemId, jdbcType=DECIMAL}
        and t2.packing_type = #{packingType, jdbcType=VARCHAR}

        	and t2.lot_number is null


        	and t2.lot_number = #{lotNumber, jdbcType=VARCHAR}
