mapper.select:
select
trx.TRX_ID, trx.TRX_NUMBER, trx.ORGANIZATION_ID,trx.TRANSFER_ORG_ID, trx.TRANSFER_TYPE, trx.STATUS,
trx.TRX_DATE, trx.REMARK,
trx.SOURCE_TYPE,
trx.SOURCE_TRX_ID, trx.OBJECT_VERSION_NUMBER,
trx.REQUEST_ID,
trx.PROGRAM_ID, trx.CREATION_DATE,
trx.CREATED_BY,
trx.LAST_UPDATED_BY,
trx.LAST_UPDATE_DATE,
trx.LAST_UPDATE_LOGIN, trx.ATTRIBUTE_CATEGORY
from INV_TRANSFER_TRX trx

    1=1

        AND trx.TRX_ID = #{trxId,jdbcType=DECIMAL}


        AND trx.SOURCE_TRX_ID = #{sourceTrxId,jdbcType=VARCHAR}


        AND trx.TRX_NUMBER = #{trxNumber,jdbcType=VARCHAR}


        AND trx.ORGANIZATION_ID = #{organizationId,jdbcType=DECIMAL}


        AND trx.TRANSFER_ORG_ID = #{transferOrgId,jdbcType=DECIMAL}


        AND trx.TRANSFER_TYPE = #{transferType,jdbcType=VARCHAR}


        AND trx.STATUS = #{status,jdbcType=VARCHAR}


        AND trx.TRX_DATE >= #{trxDateFrom,jdbcType=TIMESTAMP}


        AND trx.TRX_DATE < #{trxDateTo,jdbcType=TIMESTAMP}


        AND trx.CREATION_DATE >= #{createDateFrom,jdbcType=TIMESTAMP}


        AND trx.CREATION_DATE <= #{createDateTo,jdbcType=TIMESTAMP}



FOR UPDATE

select
trx.TRX_ID, trx.TRX_NUMBER, trx.ORGANIZATION_ID,trx.TRANSFER_ORG_ID, trx.TRANSFER_TYPE, trx.STATUS,
trx.TRX_DATE, trx.REMARK,
trx.SOURCE_TYPE,
trx.SOURCE_TRX_ID, trx.OBJECT_VERSION_NUMBER,
trx.REQUEST_ID,
trx.PROGRAM_ID, trx.CREATION_DATE,
trx.CREATED_BY,
trx.LAST_UPDATED_BY,
trx.LAST_UPDATE_DATE,
trx.LAST_UPDATE_LOGIN, trx.ATTRIBUTE_CATEGORY,
ttv.OVERALL_STATUS
from INV_TRANSFER_TRX trx
LEFT OUTER JOIN
INV_TRANSFER_TRX_V ttv
ON (trx.trx_id = ttv.trx_id)

    1=1

        AND trx.TRX_ID = #{trxId,jdbcType=DECIMAL}


        AND trx.SOURCE_TRX_ID = #{sourceTrxId,jdbcType=VARCHAR}


        AND trx.TRX_NUMBER like concat('%',concat(#{trxNumber,jdbcType=VARCHAR},'%'))


        AND trx.ORGANIZATION_ID = #{organizationId,jdbcType=DECIMAL}


        AND trx.TRANSFER_ORG_ID = #{transferOrgId,jdbcType=DECIMAL}


        AND trx.TRANSFER_TYPE = #{transferType,jdbcType=VARCHAR}


        AND trx.STATUS = #{status,jdbcType=VARCHAR}


        AND trunc(trx.TRX_DATE) >= #{trxDateFrom,jdbcType=TIMESTAMP}


        AND trunc(trx.TRX_DATE) <= #{trxDateTo,jdbcType=TIMESTAMP}


        AND trunc(trx.CREATION_DATE) >= #{createDateFrom,jdbcType=TIMESTAMP}


        AND trunc(trx.CREATION_DATE) <= #{createDateTo,jdbcType=TIMESTAMP}


        AND trx.STATUS in

            #{outStatuses}



        AND ttv.OVERALL_STATUS in

            #{overallStatuses}





    order by trx.TRX_NUMBER asc


    order by trx.TRX_NUMBER desc


    order by trx.CREATION_DATE asc


    order by trx.CREATION_DATE desc


    order by trx.TRX_DATE asc


    order by trx.TRX_DATE desc


    order by trx.TRX_NUMBER desc

select t1.TRX_ID, t1.TRX_NUMBER, t1.ORGANIZATION_ID, t1.TRANSFER_ORG_ID, t1.TRANSFER_TYPE, t1.SOURCE_TRX_ID, t1.STATUS, t1.TRX_DATE, t1.REMARK, t1.CREATION_DATE, t1.TRX_ID inTrxNumber, t2.TRX_NUMBER outTrxNumber, t1.STATUS inStatus, t2.STATUS outStatus, t2.OVERALL_STATUS from INV_TRANSFER_TRX t1, INV_TRANSFER_TRX_V t2
1=1
AND t1.TRX_ID like concat('%',concat(#{trxId,jdbcType=DECIMAL},'%'))
AND t1.TRX_NUMBER like concat('%',concat(#{trxNumber,jdbcType=VARCHAR},'%'))
AND t1.ORGANIZATION_ID = #{organizationId,jdbcType=DECIMAL}
AND t1.TRANSFER_ORG_ID = #{transferOrgId,jdbcType=DECIMAL}
AND t1.TRANSFER_TYPE = #{transferType,jdbcType=VARCHAR}
AND t1.STATUS = #{status,jdbcType=VARCHAR}
AND trunc(t1.TRX_DATE) >= #{trxDateFrom,jdbcType=TIMESTAMP}
AND trunc(t1.TRX_DATE) <= #{trxDateTo,jdbcType=TIMESTAMP}
AND trunc(t1.CREATION_DATE) >= #{createDateFrom,jdbcType=TIMESTAMP}
AND trunc(t1.CREATION_DATE) <= #{createDateTo,jdbcType=TIMESTAMP}
AND t1.TRX_NUMBER Like concat('%',concat(#{inTrxNumber,jdbcType=VARCHAR},'%'))
AND t2.TRX_NUMBER Like concat('%',concat(#{outTrxNumber,jdbcType=VARCHAR},'%'))
AND t1.STATUS in
#{inStatuses}
AND t2.STATUS in
#{outStatuses}
AND t2.OVERALL_STATUS in
#{overallStatuses}
AND t1.SOURCE_TRX_ID=t2.TRX_ID
order by t1.TRX_NUMBER asc
order by t1.TRX_NUMBER desc
order by t2.TRX_NUMBER asc
order by t2.TRX_NUMBER desc
order by t1.CREATION_DATE asc
order by t1.CREATION_DATE desc
order by t1.TRX_DATE asc
order by t1.TRX_DATE desc
order by t1.TRX_NUMBER desc

select itt.trx_id, itt.trx_number, (select coalesce(scvt.meaning, scvb.meaning) from sys_code_b scb, sys_code_value_b scvb left outer join sys_code_value_tl scvt ON (scvb.code_value_id = scvt.code_value_id AND scvt.lang = #{request.locale,jdbcType=VARCHAR}) where scvb.value = itt.status and scb.code like 'INV.TRANSFER_OUT_STATUS' and scb.code_id = scvb.code_id) status from inv_transfer_trx itt
itt.organization_id = #{request.invOrgId,jdbcType=DECIMAL} and itt.transfer_type = 'TRFOT'
and itt.trx_number = #{trxNumber, jdbcType=VARCHAR}

select itt.trx_id, itt.trx_number, (select coalesce(scvt.meaning, scvb.meaning) from sys_code_b scb, sys_code_value_b scvb left outer join sys_code_value_tl scvt ON (scvb.code_value_id = scvt.code_value_id AND scvt.lang = #{request.locale,jdbcType=VARCHAR}) where scvb.value = itt.status and scb.code like 'INV.TRANSFER_IN_STATUS' and scb.code_id = scvb.code_id) status from inv_transfer_trx itt
itt.transfer_org_id = #{request.invOrgId,jdbcType=DECIMAL} and itt.transfer_type = 'TRFIN'
and itt.trx_number = #{trxNumber, jdbcType=VARCHAR}
ORDER BY ${_colName} ${sortorder}
