mapper.select:
select ,ist.STATUS, sb.name, sc.CURRENCY_NAME from PO_HEADER ph left OUTER join INV_STOCK_TRX ist on(ph.TRX_NUMBER = ist.TRX_NUMBER) left outer join SPM_MARKET_TL sb on(ph.MARKET_ID=sb.MARKET_ID and sb.LANG = #{request.locale,jdbcType=VARCHAR}) left outer join SPM_CURRENCY sc on(ph.currency = sc.currency_code)
ph.PO_HEADER_ID=#{poHeaderId,jdbcType=DECIMAL}
ist.STATUS = #{status,jdbcType=VARCHAR},
AND Upper(ph.PO_NUMBER) like concat('%',concat(Upper(#{poNumber,jdbcType=VARCHAR}),'%'))
AND Upper(ph.VENDOR_NAME) like concat('%',concat(Upper(#{vendorName,jdbcType=VARCHAR}),'%'))
AND Upper(sb.name) like concat('%',concat(Upper(#{marketName,jdbcType=VARCHAR}),'%'))
AND ph.market_id=#{marketId,jdbcType=DECIMAL}
AND ph.ORDER_DATE >= #{orderDateTo,jdbcType=TIMESTAMP}
AND ph.ORDER_DATE <= #{orderDateDo,jdbcType=TIMESTAMP}
ORDER BY ${_colName} ${sortorder}
