mapper.select:
select C.CHANGE_ID, C.MEMBER_ID, M.MEMBER_CODE, M.CHINESE_NAME, M.ENGLISH_NAME, C.FROM_MARKET_ID, C.TO_MARKET_ID, (SELECT coalesce(smt.name, sm.name) FROM spm_market_b sm LEFT OUTER JOIN spm_market_tl smt ON (sm.market_id = smt.market_id AND smt.lang = #{request.locale,jdbcType=VARCHAR,javaType=java.lang.String}) WHERE sm.market_id = c.from_market_id) as FROM_MARKET_NAME, (SELECT coalesce(smt.name, sm.name) FROM spm_market_b sm LEFT OUTER JOIN spm_market_tl smt ON (sm.market_id = smt.market_id AND smt.lang = #{request.locale,jdbcType=VARCHAR,javaType=java.lang.String}) WHERE sm.market_id = c.to_market_id) as TO_MARKET_NAME,
C.APPLY_DATE, C.APPROVAL_STATUS, C.APPROVAL_DATE, C.REMARK, C.GDS_ID from MM_MEMBER_V M,MM_MEM_MARKET_CHANGE C
c.from_MARKET_ID = #{marketId,jdbcType=DECIMAL}
c.from_MARKET_ID = #{request.marketId,jdbcType=DECIMAL}
AND M.MEMBER_CODE = #{memberCode,jdbcType=DECIMAL}
AND C.APPROVAL_DATE >= #{beforeDate,jdbcType=TIMESTAMP}
AND C.APPROVAL_DATE <= #{afterDate,jdbcType=TIMESTAMP}
AND M.MEMBER_ID = C.MEMBER_ID
order by APPLY_DATE asc
order by APPLY_DATE desc

select GDS_ID,m.CHINESE_NAME,m.ENGLISH_NAME,m.MEMBER_CODE,
(SELECT coalesce(smt.name, sm.name) FROM spm_market_b sm LEFT OUTER JOIN spm_market_tl smt ON (sm.market_id = smt.market_id AND smt.lang = #{request.locale,jdbcType=VARCHAR,javaType=java.lang.String}) WHERE sm.market_id = c.from_market_id) as FROM_MARKET_NAME, (SELECT coalesce(smt.name, sm.name) FROM spm_market_b sm LEFT OUTER JOIN spm_market_tl smt ON (sm.market_id = smt.market_id AND smt.lang = #{request.locale,jdbcType=VARCHAR,javaType=java.lang.String}) WHERE sm.market_id = c.to_market_id) as TO_MARKET_NAME, c.CHANGE_ID, c.MEMBER_ID, c.FROM_MARKET_ID, c.TO_MARKET_ID, c.APPLY_DATE, c.APPROVAL_STATUS, c.APPROVAL_DATE,c.REMARK from MM_MEM_MARKET_CHANGE c LEFT JOIN MM_MEMBER_V m on m.MEMBER_ID = c.MEMBER_ID
c.APPROVAL_STATUS != 'P'
and c.to_market_id = #{marketId,jdbcType=DECIMAL}
and c.to_market_id = #{request.marketId,jdbcType=DECIMAL}
and m.MEMBER_CODE = #{memberCode,jdbcType=VARCHAR}
and m.MEMBER_ID = #{memberId,jdbcType=DECIMAL}
and ((upper(m.english_name) LIKE upper(concat('%', concat(#{memberName,jdbcType=VARCHAR}, '%'))) OR m.member_id IN (SELECT member_id FROM MM_MEM_RELATIONSHIP rel WHERE rel.rel_type = 'SUPSE' AND (rel.chinese_name LIKE concat('%', concat(#{memberName,jdbcType=VARCHAR}, '%')) OR upper(rel.english_name) LIKE concat('%', concat(upper(#{memberName,jdbcType=VARCHAR}), '%'))))) OR (m.chinese_name LIKE concat('%', concat(#{memberName,jdbcType=VARCHAR}, '%')) OR m.member_id IN (SELECT member_id FROM MM_MEM_RELATIONSHIP rel WHERE rel.rel_type = 'SUPSE' AND (rel.chinese_name LIKE concat('%', concat(#{memberName,jdbcType=VARCHAR}, '%')) OR upper(rel.english_name) LIKE concat('%', concat(upper(#{memberName,jdbcType=VARCHAR}), '%'))))) or ( upper(m.member_name) LIKE upper(concat('%', concat(#{memberName,jdbcType=VARCHAR}, '%'))) ) )
and (m.PHONE_NO = #{phoneNumber,jdbcType=VARCHAR} or m.OTHER_PHONE_NO = #{phoneNumber,jdbcType=VARCHAR})
and (m.BR_NUMBER = #{registerCode,jdbcType=VARCHAR} or m.ID_NUMBER = #{registerCode,jdbcType=VARCHAR} or m.member_id IN (SELECT member_id FROM MM_MEM_RELATIONSHIP rel WHERE rel.rel_type = 'SPOUS' AND rel.ID_NUMBER = #{registerCode,jdbcType=VARCHAR}) )
and c.APPROVAL_STATUS = #{approvalStatus,jdbcType=VARCHAR}
order by c.APPROVAL_DATE desc
