mapper.select:
select MSC.CHANGE_ID CHANGE_ID, MSC.MEMBER_ID MEMBER_ID, MM.ENGLISH_NAME ENGLISH_NAME, MM.CHINESE_NAME CHINESE_NAME, MSC.OPERATION_TYPE OPERATION_TYPE, MSC.APPLY_DATE APPLY_DATE, MSC.APPROVAL_STATUS APPROVAL_STATUS, MSC.APPROVAL_DATE APPROVAL_DATE, MSC.REMARK REMARK, MSC.APP_NO APP_NO, MM.MEMBER_CODE MEMBER_CODE, MSC.SYN_STATUS SYN_STATUS, coalesce(smt.name, sm.name) MARKET_NAME from MM_MEM_STATUS_CHANGE MSC,MM_MEMBER_V MM, spm_market_b sm LEFT OUTER JOIN spm_market_tl smt ON (sm.market_id = smt.market_id AND smt.lang = #{request.locale,jdbcType=VARCHAR,javaType=java.lang.String}) where MSC.MEMBER_ID=MM.MEMBER_ID AND MSC.MARKET_ID=SM.MARKET_ID
and MSC.MARKET_ID = #{marketId,jdbcType=DECIMAL}
and MSC.MARKET_ID = #{request.marketId,jdbcType=DECIMAL}
AND trunc(MSC.APPLY_DATE,'dd') >= #{beginDate,jdbcType=TIMESTAMP}
AND trunc(MSC.APPLY_DATE,'dd')<=#{endDate,jdbcType=TIMESTAMP}
AND MEMBER_CODE= #{memberCode,jdbcType=VARCHAR}
order by APPLY_DATE asc
order by APPLY_DATE desc
