mapper.select:
select 

from MM_MEM_WITHDRAW t


        t.widthraw_id = #{withdrawId,jdbcType=DECIMAL}


        t.member_id = #{memberId,jdbcType=DECIMAL}


        AND t.period >= #{periodFrom,jdbcType=VARCHAR}


        AND t.month >= #{periodTo,jdbcType=VARCHAR}

select w.WITHDRAW_ID,(SELECT coalesce(smt.name, sm.name) FROM spm_market_b sm LEFT OUTER JOIN spm_market_tl smt ON (sm.market_id = smt.market_id AND smt.lang = #{request.locale,jdbcType=VARCHAR,javaType=java.lang.String}) WHERE w.market_id = sm.market_id) as MARKET_BELONG,c.name as REF_COMPANY,m.MEMBER_CODE,m.MEMBER_ID,p.PERIOD_NAME, (select vb.MEANING from SYS_CODE_VALUE_B vb LEFT JOIN SYS_CODE_B cb on CB.CODE_ID = vb.CODE_ID where vb.value = 'RB' and CB.code = 'MM.ACCOUNTING_TYPE') as WRITEOFF_TYPE, ADJUSTMENT_TYPE,AMOUNT,w.STATUS,w.REMARK, concat(m.CHINESE_NAME,concat('/',m.ENGLISH_NAME)) as MEMBER_NAME from mm_mem_withdraw w LEFT JOIN MM_MEMBER_V m on w.MEMBER_ID = m.MEMBER_ID LEFT JOIN SPM_COMPANY_B c on c.COMPANY_ID = m.COMPANY_ID LEFT JOIN spm_period p on p.period_id = w.period
AND w.market_id = #{marketId,jdbcType=DECIMAL}
AND w.market_id = #{request.marketId,jdbcType=DECIMAL}
AND w.status = #{status,jdbcType=VARCHAR}
and m.MEMBER_CODE = #{memberCode,jdbcType=VARCHAR}
and m.MEMBER_ID = #{memberId,jdbcType=DECIMAL}
and ((upper(m.english_name) LIKE upper(concat('%', concat(#{memberName,jdbcType=VARCHAR}, '%'))) OR m.member_id IN (SELECT member_id FROM MM_MEM_RELATIONSHIP rel WHERE rel.rel_type = 'SUPSE' AND (rel.chinese_name LIKE concat('%', concat(#{memberName,jdbcType=VARCHAR}, '%')) OR upper(rel.english_name) LIKE concat('%', concat(upper(#{memberName,jdbcType=VARCHAR}), '%'))))) OR (m.chinese_name LIKE concat('%', concat(#{memberName,jdbcType=VARCHAR}, '%')) OR m.member_id IN (SELECT member_id FROM MM_MEM_RELATIONSHIP rel WHERE rel.rel_type = 'SUPSE' AND (rel.chinese_name LIKE concat('%', concat(#{memberName,jdbcType=VARCHAR}, '%')) OR upper(rel.english_name) LIKE concat('%', concat(upper(#{memberName,jdbcType=VARCHAR}), '%'))))))
and to_date(p.period_name,'yyyy-MM') >= to_date(#{periodFrom,jdbcType=VARCHAR}, 'yyyy-MM')
and to_date(p.period_name,'yyyy-MM') <= to_date(#{periodTo,jdbcType=VARCHAR}, 'yyyy-MM')
and to_date(p.period_name,'yyyy-MM') = to_date(#{periodTo,jdbcType=VARCHAR}, 'yyyy-MM')
and to_date(p.period_name,'yyyy-MM') between to_date(#{periodFrom,jdbcType=VARCHAR}, 'yyyy-MM') and to_date(#{periodTo,jdbcType=VARCHAR}, 'yyyy-MM')
ORDER BY w.WITHDRAW_ID desc

select count(m.MEMBER_ID) from MM_MEMBER m
where m.member_id = #{memberId,jdbcType=DECIMAL}
and m.STATUS = #{memberStatus,jdbcType=VARCHAR}
AND m.market_id = #{request.marketId,jdbcType=DECIMAL}

select count(b.MEMBER_ID) from MM_ACCOUNTING_BALANCE b
where b.MEMBER_ID = #{memberId,jdbcType=DECIMAL}
and b.ACCOUNTING_TYPE = #{accountingType,jdbcType=VARCHAR}
and b.BALANCE >= #{amount,jdbcType=DECIMAL}

mapper.update:
UPDATE MM_ACCOUNTING_BALANCE b
set b.BALANCE = b.BALANCE - #{amount,jdbcType=DECIMAL}
where b.MEMBER_ID = #{memberId,jdbcType=DECIMAL}
and b.ACCOUNTING_TYPE = #{accountingType,jdbcType=VARCHAR}

   update mm_mem_withdraw w
       set w.status = #{status,jdbcType = VARCHAR}
       ,source_type='BONUS'
       ,SOURCE_KEY=(SELECT bbf.bonus_id
 FROM bm_bonus_final bbf
WHERE bbf.member_id = w.member_id
      and bbf.period_id = #{combine.periodId,jdbcType = VARCHAR}
      and bbf.market_id = #{combine.marketId,jdbcType = VARCHAR}
      and bbf.bonus_type =#{combine.bonusType,jdbcType = VARCHAR})
       where w.WITHDRAW_ID in 

                   #{memWithdraw.withdrawId}
