mapper.select:
SELECT oao.autoship_number, mmm.MEMBER_CODE as MEMBER_NUMBER, oao.SALES_SCORE, oao.execute_type, oao.autoship_status, oao.execute_status, mmn.MEMBER_NAME, (SELECT SUM(oal.pv * oal.QUANTITY) FROM om_autoship_line oal WHERE oao.autoship_id = oal.autoship_id) pv_sum, (oao.order_amt + coalesce((SELECT logistics.SHIPPING_FEE FROM OM_SALES_LOGISTICS logistics where logistics.HEADER_ID =oao.AUTOSHIP_ID and autoship_flag='Y'),0)) order_amt, oao.creation_date, su.user_name, (CASE WHEN oao.AUTOSHIP_STATUS = 'TER' THEN ( SELECT su1.user_name FROM (sys_user su1 left outer join sys_acc_rel sar1 on (su1.user_id=sar1.rel_party_id and sar1.ACCOUNT_TYPE = 'USER')) left outer join sys_account sa1 on(sar1.account_id = sa1.account_id) where sa1.account_id = oao.LAST_UPDATED_BY) ELSE '' END) LAST_UPDATED_NAME,
(CASE WHEN oao.AUTOSHIP_STATUS = 'TER' THEN oao.LAST_UPDATE_DATE ELSE null END) LAST_UPDATE_DATE, oao.autoship_id ,(SELECT MAX(t.creation_date) FROM om_sales_order t WHERE t.source_type = 'AUTOS' AND T.SOURCE_KEY=autoship_number) LATEST_EXECUTE_DATE ,(SELECT MAX(t.batch_number) FROM om_sales_order t WHERE t.source_type = 'AUTOS' AND T.SOURCE_KEY=autoship_number) LATEST_BATCH_NUMBER, (SELECT t2.ORDER_NUMBER from om_sales_order t2 where t2.CREATION_DATE = (SELECT MAX(t1.CREATION_DATE) FROM om_sales_order t1 WHERE t1.source_type = 'AUTOS' AND t1.SOURCE_KEY= oao.AUTOSHIP_NUMBER) and t2.SOURCE_KEY = oao.AUTOSHIP_NUMBER) last_order_number, (SELECT t2.HEADER_ID from om_sales_order t2 where t2.CREATION_DATE = (SELECT MAX(t1.CREATION_DATE) FROM om_sales_order t1 WHERE t1.source_type = 'AUTOS' AND t1.SOURCE_KEY= oao.AUTOSHIP_NUMBER) and t2.SOURCE_KEY = oao.AUTOSHIP_NUMBER) LAST_ORDER_ID FROM om_autoship_order oao LEFT OUTER JOIN (SELECT mm.MEMBER_ID, (CASE MM.MEMBER_TYPE WHEN 'COMP' THEN COALESCE(CASE WHEN MM.REF_COMPANY IS NULL THEN MM.REF_COMPANY_EN ELSE MM.REF_COMPANY END ,'') ELSE CONCAT(CASE WHEN MM.ENGLISH_NAME IS NULL THEN '' WHEN MM.CHINESE_NAME IS NULL THEN MM.ENGLISH_NAME ELSE CONCAT(MM.ENGLISH_NAME, '/') END, MM.CHINESE_NAME) END )AS MEMBER_NAME from MM_MEMBER_V mm ) mmn on mmn.MEMBER_ID = oao.MEMBER_ID, mm_member mmm, sys_account sa, sys_acc_rel sar, sys_user su WHERE oao.member_id = mmm.member_id AND oao.created_by = sa.account_id AND sar.account_id = sa.account_id AND su.user_id = sar.rel_party_id
AND oao.SALES_ORG_ID = #{salesOrgId,jdbcType=DECIMAL}
AND oao.AUTOSHIP_NUMBER like concat('%',concat(#{autoshipNumber,jdbcType=VARCHAR},'%'))
AND oao.AUTOSHIP_STATUS in
#{status}
AND mmm.MEMBER_CODE like concat('%',concat(#{memberNumber,jdbcType=VARCHAR},'%'))
AND mmn.MEMBER_NAME like concat('%',concat(#{memberName,jdbcType=VARCHAR},'%'))
AND oao.execute_status = #{executeStatus,jdbcType=VARCHAR}
AND oao.creation_date > #{autoshipCreateDateForm,jdbcType=TIMESTAMP}
AND oao.creation_date < #{autoshipCreateDateTo,jdbcType=TIMESTAMP}
order by oao.AUTOSHIP_NUMBER ${sortorder}
order by oao.CREATION_DATE ${sortorder}

mapper.update:
update OM_AUTOSHIP_ORDER SET OBJECT_VERSION_NUMBER = OBJECT_VERSION_NUMBER+1,AUTOSHIP_STATUS = #{autoshipStatus,jdbcType=VARCHAR},
 LAST_UPDATE_DATE =CURRENT_TIMESTAMP
 where AUTOSHIP_ID = #{autoshipId,jdbcType=DECIMAL}

mapper.select:
select AUTOSHIP_ID from OM_AUTOSHIP_ORDER where (AUTOSHIP_STATUS='ACV' OR AUTOSHIP_STATUS='SUS')
AND MEMBER_ID=(SELECT MEMBER_ID FROM OM_AUTOSHIP_ORDER WHERE AUTOSHIP_ID=#{autoshipId,jdbcType=DECIMAL})
AND AUTOSHIP_ID != #{autoshipId,jdbcType=DECIMAL}

select
,(SELECT MAX(t.creation_date) FROM om_sales_order t WHERE t.source_type = 'AUTOS' AND T.SOURCE_KEY=autoship_number) LATEST_EXECUTE_DATE ,(SELECT MAX(t.batch_number) FROM om_sales_order t WHERE t.source_type = 'AUTOS' AND T.SOURCE_KEY=autoship_number) LATEST_BATCH_NUMBER from OM_AUTOSHIP_ORDER oao where oao.AUTOSHIP_STATUS='ACV'
and oao.MEMBER_ID = #{memberId,jdbcType=DECIMAL}
and oao.SALES_ORG_ID = #{request.salesOrgId,jdbcType=DECIMAL}
