mapper.select:
  	select SUM(rl.QUANTITY)
  	from OM_SALES_RTN_LINE rl
  	where rl.ORDER_LINE_ID = #{orderLineId, jdbcType=DECIMAL}
  	     and rl.RETURN_ID in (select sr.RETURN_ID 
  	     					  from OM_SALES_RETURN sr

  	     					  	1=1

						       		AND sr.RETURN_ID != #{returnId,jdbcType=DECIMAL}


  	     					  )
  	     GROUP BY( rl.ORDER_LINE_ID)

select v.COUNT_STOCK_FLAG, v.LOT_CONTROL_FLAG 
from inv_item_property_v v 
where v.item_id = #{itemId, jdbcType=DECIMAL} and v.ORGANIZATION_ID = #{orgId, jdbcType=DECIMAL}

SELECT osrl.RETURN_LINE_ID, osrl.RETURN_ID, osrl.SALES_ORG_ID, osrl.ORDER_LINE_ID, osrl.ITEM_ID, osrl.COUNT_ITEM_ID, osrl.QUANTITY, osrl.RETURN_PROMOTION, osrl.RETURN_INV_FLAG, osrl.CONVERSION_RATE, salesline.ITEM_NUMBER, salesline.ITEM_NAME, salesline.ITEM_TYPE, salesline.COUNT_STOCK_FLAG, salesline.COUNT_ITEM_NUMBER, salesline.COUNT_ITEM_NAME, salesline.UNIT_REDEEM_POINT, salesline.UNIT_SELLING_PRICE, salesline.UNIT_DISCOUNT_PRICE, salesline.UOM_NAME, salesline.LOT_CTRL_FLAG, salesline.ORDER_QUANTITY, salesline.OUTSTANDING_QTY, salesline.DISCOUNT_TYPE, salesline.DISCOUNT_VALUE, salesline.ADJUSTMENT_AMOUNT, salesline.PV, osrl.QUANTITY, (salesline.OUTSTANDING_QTY-salesline.return_qty) ENABLED_RTN_QUANTITY, salesline.PARENT_LINE_ID, salesline.COUNT_TYPE, salesline.DISABLE_TAX_FLAG FROM OM_SALES_RTN_LINE osrl LEFT OUTER JOIN ( SELECT osl.line_id ,osl.item_id ,itemuom.item_number ,itemuom.name uom_name ,itemuom.item_type ,itl.item_name ,countitem.item_id count_item_id ,countitem.item_number count_item_number ,countitem.item_name count_item_name ,countitem.count_stock_flag count_stock_flag ,countitem.lot_control_flag lot_ctrl_flag ,countitem.count_type count_type ,osl.unit_selling_price unit_selling_price ,osl.unit_discount_price unit_discount_price ,osl.unit_redeem_point unit_redeem_point ,osl.quantity order_quantity ,coalesce(deliveryline.outstanding_qty,0) outstanding_qty ,coalesce(returnline.return_qty,0) return_qty ,pv.discount_type discount_type ,pv.discount_value discount_value ,osa.adjustment_amount adjustment_amount ,osl.pv pv ,osl.parent_line_id parent_Line_Id ,osl.ATTRIBUTE1 disable_tax_flag FROM om_sales_line osl LEFT OUTER JOIN om_sales_adjustment osa ON (osa.header_id = osl.header_id AND osa.adjustment_type = 'CP') LEFT OUTER JOIN pdm_voucher pv ON (pv.voucher_id = osl.voucher_id) LEFT OUTER JOIN inv_item_tl itl ON (osl.item_id = itl.item_id AND itl.lang = #{request.locale, jdbcType=VARCHAR}) LEFT OUTER JOIN ( SELECT iib.item_id ,iib.item_number ,iib.item_type ,uomtl.name FROM inv_item_b iib LEFT OUTER JOIN inv_unit_of_measure_tl uomtl ON (uomtl.uom_code = iib.uom_code AND uomtl.lang = #{request.locale, jdbcType=VARCHAR}) ) itemuom ON (itemuom.item_id = osl.item_id) LEFT OUTER JOIN ( SELECT dodl.order_line_id ,SUM(dodl.outstanding_qty) outstanding_qty FROM dm_order_delivery_line dodl LEFT JOIN dm_order_delivery dd ON dd.delivery_id = dodl.delivery_id WHERE dd.delivery_status IN ('PCKUP', 'SHIPP') GROUP BY (dodl.order_line_id) ) deliveryline ON osl.line_id = deliveryline.order_line_id LEFT OUTER JOIN ( SELECT osrl.ORDER_LINE_ID, SUM(osrl.QUANTITY) RETURN_QTY FROM OM_SALES_RTN_LINE osrl LEFT OUTER JOIN OM_SALES_RETURN osr ON (osr.RETURN_ID = osrl.RETURN_ID ) -- where osr.RETURN_STATUS != 'NEW'
1=1
AND osr.RETURN_ID != #{returnId, jdbcType=DECIMAL}
GROUP BY(osrl.ORDER_LINE_ID) ) returnline ON osl.line_id = returnline.order_line_id LEFT OUTER JOIN ( SELECT v.ORGANIZATION_ID, iib.ITEM_ID, v.ITEM_ID COUNT_ITEM_ID, v.ITEM_NAME, v.ITEM_NUMBER, v.ITEM_TYPE, v.COUNT_STOCK_FLAG, v.LOT_CONTROL_FLAG, v.COUNT_TYPE FROM inv_item_b iib LEFT OUTER JOIN inv_item_property_v v ON(COALESCE(iib.COUNT_ITEM_ID, iib.ITEM_ID) = v.ITEM_ID) LEFT OUTER JOIN inv_item_tl itl ON (iib.COUNT_ITEM_ID = itl.ITEM_ID AND itl.lang = #{request.locale, jdbcType=VARCHAR}) ) countitem ON (countitem.item_id = osl.item_id AND countitem.organization_id = #{invOrgId,jdbcType=DECIMAL}) WHERE EXISTS ( SELECT 1 FROM inv_item_assign iia WHERE iia.item_id = osl.item_id AND iia.assign_type = 'INV' AND iia.assign_value = #{invOrgId, jdbcType=DECIMAL})) salesline ON salesline.line_id = osrl.order_line_id
osrl.RETURN_ID = #{returnId, jdbcType=DECIMAL}
AND salesline.parent_Line_Id = #{parentLineId, jdbcType=DECIMAL}
AND salesline.parent_Line_Id IS NULL

select 
from OM_SALES_RTN_LINE
where RETURN_ID = #{returnId,jdbcType=DECIMAL} 
  and SALES_ORG_ID = #{salesOrgId,jdbcType=DECIMAL}
