mapper.select:
select pva.VOUCHER_ID, pv.CATEGORY, pv.DISCOUNT_TYPE, pv.DISCOUNT_VALUE, pv.DESCRIPTION, pv.START_ACTIVE_DATE, pv.END_ACTIVE_DATE, pv.APP_SCOPE, pv.APPLY_CRITERIA, pv.USAGE_MODE, pv.TYPE, pv.VOUCHER_CODE, pv.NAME from PDM_VOUCHER_ASSIGN pva left outer join PDM_VOUCHER pv on pva.VOUCHER_ID = pv.VOUCHER_ID where pva.MEMBER_ID = #{memberId,jdbcType=DECIMAL}
and pv.ENABLED_FLAG = 'Y' and pv.START_ACTIVE_DATE <= CURRENT_TIMESTAMP and pv.END_ACTIVE_DATE >= CURRENT_TIMESTAMP
and pv.ENABLED_FLAG = 'Y' and pv.END_ACTIVE_DATE <= CURRENT_TIMESTAMP
and pv.ENABLED_FLAG = 'N'
and pv.APPLY_CRITERIA = #{scope,jdbcType=VARCHAR}

select VOUCHER_ID, MARKET_ID, SALES_ORG_ID, VOUCHER_CODE, NAME, DESCRIPTION, ENABLED_FLAG, START_ACTIVE_DATE, END_ACTIVE_DATE, CATEGORY, TYPE, QUANTITY, MAX_USE_QTY, STATUS, APP_SCOPE, USAGE_MODE, APPLY_CRITERIA, ITEM_QUANTITY, GIFT_QUANTITY, APP_TAX, OPERATION, ORDER_AMOUNT, USE_CRITERIA, DISCOUNT_TYPE, DISCOUNT_VALUE, OBJECT_VERSION_NUMBER, REQUEST_ID, PROGRAM_ID, CREATION_DATE, CREATED_BY, LAST_UPDATED_BY, LAST_UPDATE_DATE, LAST_UPDATE_LOGIN, ATTRIBUTE_CATEGORY, ATTRIBUTE1, ATTRIBUTE2, ATTRIBUTE3, ATTRIBUTE4, ATTRIBUTE5, ATTRIBUTE6, ATTRIBUTE7, ATTRIBUTE8, ATTRIBUTE9, ATTRIBUTE10, ATTRIBUTE11, ATTRIBUTE12, ATTRIBUTE13, ATTRIBUTE14, ATTRIBUTE15 from PDM_VOUCHER
VOUCHER_ID = #{voucherId,jdbcType=DECIMAL}
AND SALES_ORG_ID=#{salesOrgId,jdbcType=DECIMAL}
AND Upper(VOUCHER_CODE)like concat('%',concat(Upper(#{voucherCode,jdbcType=VARCHAR}),'%'))
AND Upper(NAME)like concat('%',concat(Upper(#{name,jdbcType=VARCHAR}),'%'))
AND Upper(DESCRIPTION)like concat('%',concat(Upper(#{description,jdbcType=VARCHAR}),'%'))
AND ENABLED_FLAG = #{enabledFlag,jdbcType=DECIMAL}
AND APPLY_CRITERIA = #{applyCriteria,jdbcType=VARCHAR}
AND START_ACTIVE_DATE >= #{startActiveDate,jdbcType=TIMESTAMP}
AND END_ACTIVE_DATE <= #{endActiveDate,jdbcType=TIMESTAMP}
order by VOUCHER_CODE desc
order by VOUCHER_CODE desc
order by VOUCHER_CODE asc
order by START_ACTIVE_DATE asc
order by START_ACTIVE_DATE desc
order by END_ACTIVE_DATE asc
order by END_ACTIVE_DATE desc

mapper.update:
update PDM_VOUCHER 
  set ENABLED_FLAG = 'N', 
      LAST_UPDATE_DATE = CURRENT_TIMESTAMP 

    CATEGORY = 'VIP'

        AND VOUCHER_ID = #{voucherId,jdbcType=DECIMAL}


        AND VOUCHER_CODE = #{voucherCode,jdbcType=VARCHAR}

mapper.select:
SELECT sm.code market_code, mm.member_code, pv.category, pv.voucher_code, pv.name, pv.enabled_flag, pv.start_active_date, pv.end_active_date, pva.quantity, pv.order_amount, pv.APP_TAX, ( SELECT max(osoo.order_number) FROM om_sales_order osoo, om_mws_order_payment op
WHERE osoo.header_id = op.order_header_id AND op.payment_method = 'ECUP' AND OP.VOUCHER_ID = pv.VOUCHER_ID AND OP.STATUS = 'NEW' AND osoo.order_status IN ('WPAY', 'PAYIN', 'COMP') ) order_number FROM spm_market_b sm, mm_member mm, pdm_voucher_assign pva, pdm_voucher pv WHERE sm.market_id = mm.market_id AND mm.member_id = pva.member_id AND pva.voucher_id = pv.voucher_id
AND pva.assign_type = 'MEM'
AND pv.category = 'VIP'
AND sm.code = #{market,jdbcType=VARCHAR}
AND mm.member_code = #{memberCode,jdbcType=VARCHAR}
AND pv.voucher_code IN
#{voucherCode}
AND pv.STATUS = 'RELD' and Pv.ENABLED_FLAG = 'Y' AND pv.START_ACTIVE_DATE <= CURRENT_TIMESTAMP AND ( pv.END_ACTIVE_DATE >= CURRENT_TIMESTAMP OR pv.END_ACTIVE_DATE IS NULL )
