mapper.select:
select sar.account_id,smma.ASSIGN_ID, smma.MS_MESSAGE_ID, smma.ASSIGN_TYPE, smma.ASSIGN_VALUE, mmv.member_id,mmv.member_name,mmv.email,mmv.area_code,mmv.phone_no,mmv.STATUS,mmv.member_code from SYS_MS_MESSAGE_ASSIGN smma left outer join MM_MEMBER_v mmv on(smma.ASSIGN_VALUE = mmv.member_id) left outer join sys_acc_rel sar on(mmv.member_id=sar.REL_PARTY_ID and sar.ACCOUNT_TYPE='MEM') where smma.assign_type='MEM'
and smma.MS_MESSAGE_ID=#{msMessageId,jdbcType=DECIMAL}
and smma.ASSIGN_VALUE=#{assignValue,jdbcType=VARCHAR}

select smmr.MS_MESSAGE_ID, smmr.ASSIGN_VALUE, smmr.PUBLISH_STATUS, mmv.member_code from SYS_MS_MESSAGE_RESULT smmr left outer join MM_MEMBER_v mmv on(smmr.ASSIGN_VALUE = mmv.member_id) where smmr.assign_type='MEM'
and smmr.MS_MESSAGE_ID=#{msMessageId,jdbcType=DECIMAL}
AND Upper(mmv.member_code) like concat('%',concat(Upper(#{memberCode,jdbcType=VARCHAR}),'%'))
