mapper.select:
select account_id, login_name, password,status,pwd_expiry_date
from sys_account
where login_name = #{loginName,jdbcType=VARCHAR}

select

from SYS_ACCOUNT



        account_id = #{accountId,jdbcType=DECIMAL}


        AND upper(login_name) = upper(#{loginName,jdbcType=VARCHAR})


        AND status = #{status,jdbcType=VARCHAR}

mapper.update:
update SYS_ACCOUNT
PASSWORD = #{password,jdbcType=VARCHAR}, PWD_EXPIRY_DATE = #{pwdExpiryDate,jdbcType=TIMESTAMP}, OBJECT_VERSION_NUMBER = COALESCE(OBJECT_VERSION_NUMBER,0) + 1, REQUEST_ID = #{requestId,jdbcType=DECIMAL}, PROGRAM_ID = #{programId,jdbcType=DECIMAL}, LAST_UPDATED_BY = #{lastUpdatedBy,jdbcType=DECIMAL}, LAST_UPDATE_DATE = CURRENT_TIMESTAMP, LAST_UPDATE_LOGIN = #{lastUpdateLogin,jdbcType=DECIMAL},
FIRST_LOGIN_FLAG = #{firstLoginFlag,jdbcType=VARCHAR}
FIRST_LOGIN_FLAG = 'N'
where ACCOUNT_ID = #{accountId,jdbcType=DECIMAL}

mapper.select:
select sa.login_name, sa.password, sa.status from sys_account sa, sys_acc_rel sar, mm_member mm, spm_market_b sm
sa.account_id = sar.account_id and sar.account_type = 'MEM' and sar.rel_party_id = mm.member_id and mm.market_id = sm.market_id and mm.member_code = #{distributorNumber,jdbcType=VARCHAR}
and sm.code = #{market,jdbcType=VARCHAR}
