mapper.select:
SELECT mess.account_id ,mess.account_code ,mess.user_name ,mess.market_id ,mess.account_type ,smtl.name as market_name FROM (SELECT smea.account_id ,smea.account_code ,smea.user_name ,smea.market_id ,'EMAIL' account_type FROM sys_message_email_account smea UNION ALL SELECT smsa.account_id ,smsa.account_code ,smsa.user_name ,smsa.market_id ,'SMS' account_type FROM sys_message_sms_account smsa) mess left outer join spm_market_tl smtl on(mess.market_id = smtl.market_id and smtl.LANG = #{request.locale,jdbcType=VARCHAR})
and account_type = #{accountType,jdbcType=VARCHAR}
and user_name = #{userName,jdbcType=VARCHAR}
and ACCOUNT_ID = #{accountId,jdbcType=DECIMAL}
and ACCOUNT_CODE like concat('%',concat(#{accountCode,jdbcType=VARCHAR},'%'))
and DESCRIPTION like concat('%',concat(#{description,jdbcType=VARCHAR},'%'))
ORDER BY ${_colName} ${sortorder}

select account.ACCOUNT_ID, account.ACCOUNT_CODE, account.HOST, account.PORT, account.TRY_TIMES, account.USER_NAME, account.MARKET_ID, SPM_MARKET_B.name as MARKET_NAME, account.OBJECT_VERSION_NUMBER, account.REQUEST_ID, account.PROGRAM_ID, account.CREATION_DATE, account.CREATED_BY, account.LAST_UPDATED_BY, account.LAST_UPDATE_DATE, account.LAST_UPDATE_LOGIN, account.ATTRIBUTE_CATEGORY, account.ATTRIBUTE1, account.ATTRIBUTE2, account.ATTRIBUTE3, account.ATTRIBUTE4, account.ATTRIBUTE5, account.ATTRIBUTE6, account.ATTRIBUTE7, account.ATTRIBUTE8, account.ATTRIBUTE9, account.ATTRIBUTE10, account.ATTRIBUTE11, account.ATTRIBUTE12, account.ATTRIBUTE13, account.ATTRIBUTE14, account.ATTRIBUTE15, account.DESCRIPTION, account.ORGANIZATION, account.USE_WHITE_LIST from SYS_MESSAGE_SMS_ACCOUNT account left join SPM_MARKET_B on SPM_MARKET_B.MARKET_ID = account.MARKET_ID
and account.ACCOUNT_ID = #{accountId,jdbcType=DECIMAL}
and account.ACCOUNT_CODE like concat('%',concat(#{accountCode,jdbcType=VARCHAR},'%'))
and account.DESCRIPTION like concat('%',concat(#{description,jdbcType=VARCHAR},'%'))
ORDER BY ${_colName} ${sortorder}

select 

FROM SYS_MESSAGE_SMS_ACCOUNT acc


		acc.ACCOUNT_ID != #{accountId,jdbcType=DECIMAL}

	AND
	(

		acc.ACCOUNT_CODE = #{accountCode,jdbcType=VARCHAR}



		acc.MARKET_ID = #{marketId,jdbcType=DECIMAL}


		OR acc.MARKET_ID = #{marketId,jdbcType=DECIMAL}


	)
