mapper.select:
select b.RESOURCE_ID, b.URL, b.TYPE, t.NAME, t.DESCRIPTION, b.ACCESS_CHECKED,b.LOGIN_REQUIRE, b.OBJECT_VERSION_NUMBER, b.REQUEST_ID, b.PROGRAM_ID, b.CREATION_DATE, b.CREATED_BY, b.LAST_UPDATED_BY, b.LAST_UPDATE_DATE, b.LAST_UPDATE_LOGIN, b.ATTRIBUTE_CATEGORY, b.ATTRIBUTE1, b.ATTRIBUTE2, b.ATTRIBUTE3, b.ATTRIBUTE4, b.ATTRIBUTE5, b.ATTRIBUTE6, b.ATTRIBUTE7, b.ATTRIBUTE8, b.ATTRIBUTE9, b.ATTRIBUTE10, b.ATTRIBUTE11, b.ATTRIBUTE12, b.ATTRIBUTE13, b.ATTRIBUTE14, b.ATTRIBUTE15 from SYS_RESOURCE_B b LEFT OUTER JOIN SYS_RESOURCE_TL t on(b.resource_id = t.resource_id and t.lang = #{request.locale,jdbcType=VARCHAR})
b.url like concat('%',concat(#{url,jdbcType=VARCHAR},'%'))
AND b.type = #{type,jdbcType=VARCHAR}
AND t.name like concat('%',concat(#{name,jdbcType=VARCHAR},'%'))
AND b.login_require = #{loginRequire,jdbcType=VARCHAR}

select b.RESOURCE_ID, t.NAME, t.DESCRIPTION
  from SYS_RESOURCE_B b, SYS_RESOURCE_TL t, SYS_FUNCTION_RESOURCE sfr
 where b.resource_id = t.resource_id
   and t.lang = #{request.locale,jdbcType=VARCHAR}
   and b.RESOURCE_ID = sfr.RESOURCE_ID
   and sfr.FUNCTION_ID = #{functionId,jdbcType=DECIMAL}
